DROP TABLE IF EXISTS `browse`;
CREATE TABLE `browse`(
    `id` CHAR(8) NOT NULL   COMMENT '主键' ,
    `user_id` CHAR(8) NOT NULL   COMMENT '用户id' ,
    `data_id` CHAR(8) NOT NULL   COMMENT '数据id' ,
