DROP TABLE IF EXISTS `cms`;
CREATE TABLE `cms`(
    `id` CHAR(8) NOT NULL   COMMENT '主键' ,
    `text` VARCHAR(1000) NOT NULL   COMMENT '内容' ,
    `type` SMALLINT(1) NOT NULL   COMMENT '内容类型' ,
